import { ProductType } from "../../../shared/types/product";
import api from "./axios";

export const fetchProductsByCategory = async (params: any) => {
  const response = await api.get(`/shop`, { params });
  return response.data;
};
export const fetchProducts = async () => {
  const response = await api.get(`/shop`);
  return response.data;
};
export const fetchProduct = async (id: number): Promise<ProductType[]> => {
  const response = await api.get(`/shop/${id}`);
  return response.data;
};
