import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import "./Navigation.css";
import { useNav } from "../../context";
import { logo, close, search } from "../../assets";
import SearchBar from "../SearchBar";

const Navigation = () => {
  const { activeLink, setActiveLink, openNav, closeNav } = useNav();
  const isMobile = window.matchMedia("min-width: 470px");
  // useEffect(() => {
  //   if (isMobile) {
  //     openNav();
  //   } else {
  //     closeNav();
  //   }
  // }, [isMobile]);
  return (
    <nav id="nav">
      <div className="nav-top">
        <img className="logo" src={logo} alt="Logo" />
        <SearchBar />
        <button onClick={closeNav}>
          <img
            className="icon close-btn"
            src={close}
            alt="Close"
            onClick={closeNav}
          />
        </button>
      </div>
      <ul>
        <li>
          <Link
            to={"/"}
            onClick={() => setActiveLink("home")}
            className={activeLink === "home" ? "active-link" : ""}
          >
            Home{" "}
          </Link>
        </li>
        <li>
          <Link
            onClick={() => setActiveLink("men")}
            className={activeLink === "men" ? "active-link" : ""}
          >
            Men
          </Link>
        </li>
        <li>
          <Link
            onClick={() => setActiveLink("women")}
            className={activeLink === "women" ? "active-link" : ""}
          >
            Women
          </Link>
        </li>
        <li>
          <Link
            onClick={() => setActiveLink("new arrivals")}
            to={"/new-arrivals"}
            className={activeLink === "new arrivals" ? "active-link" : ""}
          >
            New Arrivals
          </Link>
        </li>
        <li>
          <Link
            onClick={() => setActiveLink("releases")}
            className={activeLink === "releases" ? "active-link" : ""}
          >
            Releases
          </Link>
        </li>
      </ul>
    </nav>
  );
};

export default Navigation;
