import React, { useEffect } from "react";
import { Link, NavLink } from "react-router-dom";
import "./Navigation.css";
import { useNav } from "../../context";
import { logo, close as closeIcon } from "../../assets";
import SearchBar from "../SearchBar";
import { FocusTrap } from "focus-trap-react";

interface NavigationProps {
  onClose?: () => void;
}
const Navigation = ({ onClose }: NavigationProps) => {
  const { closeNav: close, isNavOpen } = useNav();
  const closeNav = React.useCallback(() => {
    close();
    onClose && onClose();
  }, [close, onClose]);
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.key === "Escape") {
        closeNav();
      }
    };
    document.addEventListener("keydown", handleKeyDown);
    return () => {
      document.removeEventListener("keydown", handleKeyDown);
    };
  }, [closeNav]);
  return (
    <FocusTrap active={isNavOpen}>
      <nav id="nav" className="closed">
        {isNavOpen && (
          <div className="nav-top">
            <Link to="/">
              <img className="logo" src={logo} alt="Logo" />
            </Link>

            <SearchBar />
            <button aria-controls="nav" autoFocus onClick={closeNav}>
              <img
                className="icon close-btn"
                src={closeIcon}
                alt="Close"
                onClick={closeNav}
              />
            </button>
          </div>
        )}
        <ul>
          <li>
            <NavLink to="/">
              {({ isActive }) => (
                <span className={isActive ? "active" : ""}>Home</span>
              )}
            </NavLink>
          </li>
          <li>
            <NavLink to="/shop/category//men">
              {({ isActive }) => (
                <span className={isActive ? "active" : ""}>Men</span>
              )}
            </NavLink>
          </li>
          <li>
            <NavLink to="/shop/category/women">
              {({ isActive }) => (
                <span className={isActive ? "active" : ""}>Women</span>
              )}
            </NavLink>
          </li>
          <li>
            <NavLink to={"/new-arrivals"}>
              {({ isActive }) => (
                <span className={isActive ? "active" : ""}>New Arrivals</span>
              )}
            </NavLink>
          </li>
          <li>
            <NavLink to={"/kids"}>
              {({ isActive }) => (
                <span className={isActive ? "active" : ""}>Kids</span>
              )}
            </NavLink>
          </li>
          <li>
            <NavLink to={"/releases"}>
              {({ isActive }) => (
                <span className={isActive ? "active" : ""}>Releases</span>
              )}
            </NavLink>
          </li>
        </ul>
      </nav>
    </FocusTrap>
  );
};

export default Navigation;
