import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { ProductType } from "../../../../shared/types/product";
import { fetchProducts } from "../../api/products";
import { ProductGridItem } from "../../components";
import { capitalize } from "../../utils";

const Shop: React.FC = () => {
  const { category = "All", subcategory } = useParams();
  const [products, setProducts] = useState<ProductType[]>([]);
  const [title, setTitle] = useState("");
  const formatTitle = (category: string): string => {
    const lower = category.toUpperCase();
    if (lower === "men" || lower === "women" || lower === "kid")
      return (category += "'S");
    return category;
  };
  useEffect(() => {
    // Build query parameters
    const params: Record<string, string> = { category: category! };
    if (subcategory) params.subcategory = subcategory;

    setTitle(
      subcategory
        ? `${formatTitle(category)} → ${formatTitle(subcategory)}`
        : `${formatTitle(category)}`
    );

    fetchProducts()
      .then((res) => setProducts(res))
      .catch((err) => console.error("Error fetching products", err));
  }, [category, subcategory]);
  return (
    <div className="shop-page" style={{ padding: "2rem" }}>
      <h1>Shop {formatTitle(title)} Collection</h1>
      {/* Product listing will go here */}
      <div className="content-grid">
        {products.map((item) => (
          <ProductGridItem item={item} />
        ))}
      </div>
    </div>
  );
};

export default Shop;
