import React, { forwardRef } from "react";
import { useCart } from "../../../context";
import { assets } from "../../../assets";
import "./CartButton.css";
import { useCartStore } from "../../../stores";

interface CartButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  badgeStyle?: React.CSSProperties;
}
const CartButton = forwardRef<HTMLButtonElement, CartButtonProps>(
  ({ badgeStyle, ...props }, ref) => {
    const { openCart, cart } = useCartStore();

    return (
      <button
        aria-label="View cart"
        ref={ref}
        className="cart-btn"
        onClick={openCart}
        {...props}
      >
        <img className="icon" src={assets.cart} alt="cart" />
        {cart.length > 0 && (
          <div className="cart-badge" onClick={openCart} style={badgeStyle} />
        )}
      </button>
    );
  }
);
export default CartButton;
