import {
  ProductBrand,
  ProductCategory,
  ProductFlag,
} from "../../../shared/types";
import api from "./axios";

export const fetchCategories = async (): Promise<ProductCategory[]> => {
  const response = await api.get(`/tags/categories`);
  return response.data;
};

export const fetchSubcategories = async (): Promise<ProductCategory[]> => {
  const response = await api.get(`/tags/subcategories`);
  return response.data;
};

export const fetchBrands = async (): Promise<ProductBrand[]> => {
  const response = await api.get(`/tags/brands`);
  return response.data;
};

export const fetchFlags = async (): Promise<ProductFlag[]> => {
  const response = await api.get(`/tags/flags`);
  return response.data;
};
