import React, { useEffect, useState } from "react";
import { Product } from "../../../../../shared/types";
import { useParams } from "react-router-dom";
import { fetchProduct } from "../../../api/admin/products";
import { ProductItem } from "../../../components";

const ProductPanel = () => {
  const [product, setProduct] = useState<Product | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [images, setImages] = useState<string[]>([]);
  const { id } = useParams();
  useEffect(() => {
    if (!id) {
      return;
    }
    fetchProduct(id)
      .then((res) => {
        setProduct(res);
      })
      .catch((err) => {
        console.error(err);
        setError(err.message);
      });
  }, [id]);
  if (error) {
    return (
      <div className="content-centered">
        <h1>Uh oh, an error occured.</h1>
      </div>
    );
  }
  if (!product) {
    return (
      <div className="content-centered">
        <h1>Uh oh, we coudn't find this product.</h1>

        {error && <p>{error}</p>}
      </div>
    );
  }
  const onAddImageClick = () => {
    images;
  };

  return (
    <div>
      <h1>{product.name}</h1>
      <div className="content-body">
        <form action="">
          <label htmlFor="image-url">Product Name</label>
          <input
            className="text-input"
            id="image-url"
            type="text"
            defaultValue={product.name}
            value={product.name}
          />
          <button>Update</button>
          <label htmlFor="image-url">Images</label>
          <div>
            {product.images.map((item) => (
              <input
                className="text-input"
                id="image-url"
                type="text"
                defaultValue={product.image_url}
              />
            ))}
            <button type="button" onClick={onAddImageClick}></button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProductPanel;
