import React, { useEffect } from "react";
import { Link, NavLink } from "react-router-dom";
import "./NavBar.css";
import { useNav } from "../../context";
import { logo, close as closeIcon } from "../../assets";
import SearchBar from "../SearchBar";
import { FocusTrap } from "focus-trap-react";

interface NavigationProps {
  onClose?: () => void;
  onNavigate?: () => void;
  disabled?: boolean;
}
const NavBar = ({ disabled, onClose, onNavigate }: NavigationProps) => {
  return (
    <FocusTrap active={isNavOpen}>
      <nav id="nav" className="closed">
        {isNavOpen && (
          <div className="nav-top">
            <button
              tabIndex={disabled ? -1 : undefined}
              aria-hidden={disabled}
              aria-controls="nav"
              autoFocus
              className="close-btn"
              onClick={closeNav}
            >
              <img
                className="icon"
                src={closeIcon}
                alt="Close"
                onClick={closeNav}
              />
            </button>
            <SearchBar
              tabIndex={disabled ? -1 : undefined}
              aria-hidden={disabled}
            />
            <Link onClick={onNavigate} to="/">
              <img className="logo" src={logo} alt="Logo" />
            </Link>
          </div>
        )}
        <ul>
          <li>
            <NavLink
              tabIndex={disabled ? -1 : undefined}
              aria-hidden={disabled}
              onClick={onNavigate}
              to="/"
            >
              {({ isActive }) => (
                <span className={isActive ? "active" : ""}>Home</span>
              )}
            </NavLink>
          </li>
          <li>
            <NavLink
              tabIndex={disabled ? -1 : undefined}
              aria-hidden={disabled}
              to="/shop/category/men"
            >
              {({ isActive }) => (
                <span className={isActive ? "active" : ""}>Men</span>
              )}
            </NavLink>
          </li>
          <li>
            <NavLink
              tabIndex={disabled ? -1 : undefined}
              aria-hidden={disabled}
              to="/shop/category/women"
            >
              {({ isActive }) => (
                <span className={isActive ? "active" : ""}>Women</span>
              )}
            </NavLink>
          </li>
          <li>
            <NavLink
              tabIndex={disabled ? -1 : undefined}
              aria-hidden={disabled}
              to={"/kids"}
            >
              {({ isActive }) => (
                <span className={isActive ? "active" : ""}>Kids</span>
              )}
            </NavLink>
          </li>
          <li>
            <NavLink
              tabIndex={disabled ? -1 : undefined}
              aria-hidden={disabled}
              to={"/new-arrivals"}
            >
              {({ isActive }) => (
                <span className={isActive ? "active" : ""}>New Arrivals</span>
              )}
            </NavLink>
          </li>

          <li>
            <NavLink
              tabIndex={disabled ? -1 : undefined}
              aria-hidden={disabled}
              to={"/releases"}
            >
              {({ isActive }) => (
                <span className={isActive ? "active" : ""}>Releases</span>
              )}
            </NavLink>
          </li>
        </ul>
      </nav>
    </FocusTrap>
  );
};

export default NavBar;
