import React, { useEffect, useRef, useState } from "react";
import { Link } from "react-router-dom";
import "./Header.css";
import { heart, logo } from "../../assets";
import { useMediaQuery } from "@react-hook/media-query";
import SearchBar from "../SearchBar";
import { CartButton, MenuButton } from "../buttons/";
import NavBar from "../NavBar";
import SideBar from "../SideBar";
function Header() {
  const [showMenu, setShowMenu] = useState(false);
  const [isNavOpen, setIsNavOpen] = useState(false);
  const menuRef = useRef<HTMLButtonElement>(
    null
  ) as React.RefObject<HTMLButtonElement>;
  const isDesktop = useMediaQuery("screen and (min-width: 720px)");
  // useEffect(() => {
  //   if (isDesktop) {
  //     closeNav();
  //   }
  // }, [isDesktop, closeNav]);
  const onOpenNav = () => {
    setIsNavOpen(true);
  };
  const onCloseNav = () => {
    setIsNavOpen(false);
  };
  useEffect(() => {
    const handleScroll = () => {
      const header = document.querySelector("header");
      const headerHeight = header
        ? header.getBoundingClientRect().height + 100
        : 80;
      setShowMenu(window.scrollY > headerHeight);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <header>
      <div className="header-container">
        <div className="header-left">
          <Link
            tabIndex={showMenu ? -1 : undefined}
            aria-hidden={showMenu}
            to={"/"}
          >
            {" "}
            <img className="logo" src={logo} alt="logo" />
          </Link>
          <div style={{ display: "flex" }}>
            <button
              tabIndex={showMenu ? -1 : undefined}
              aria-hidden={showMenu}
              id="sign-up"
            >
              Sign Up
            </button>
            <button
              tabIndex={showMenu ? -1 : undefined}
              aria-hidden={showMenu}
              id="log-in"
            >
              Log In
            </button>
          </div>
        </div>

        <div className="header-right">
          <MenuButton
            disabled={showMenu}
            aria-controls="nav"
            aria-expanded={!isDesktop ? isNavOpen : undefined}
            onClick={onOpenNav}
          />

          <SearchBar
            tabIndex={showMenu ? -1 : undefined}
            aria-hidden={showMenu}
          />
          <Link
            tabIndex={showMenu ? -1 : undefined}
            aria-hidden={showMenu}
            to={"/favorites"}
          >
            <img className="heart" src={heart} alt="heart" />
          </Link>
          <CartButton disabled={showMenu} />
        </div>
      </div>

      {/* {(showMenu || isNavOpen) && (
        <SideBar onClose={onCloseNav} isOpen={isNavOpen} openButton={menuRef}>
          <NavBar className="secondary" />
        </SideBar>
      )} */}
      {!showMenu && !isNavOpen && <NavBar className="main" />}

      <div
        className={`sticky-header
        ${showMenu ? "open" : "closed"}
         ${showMenu && !isNavOpen ? "visible" : "hidden"}
          `}
      >
        <MenuButton
          ref={menuRef}
          aria-controls="nav"
          disabled={showMenu}
          aria-expanded={!isDesktop ? isNavOpen : undefined}
          onClick={onOpenNav}
        />
        <div className="content-row">
          <Link
            tabIndex={showMenu ? -1 : undefined}
            aria-hidden={showMenu}
            to={"/favorites"}
          >
            <img className="icon" src={heart} alt="heart" />
          </Link>
          <CartButton badgeStyle={{ borderColor: "white" }} />
        </div>
      </div>
    </header>
  );
}

export default Header;
