import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Product } from "../../../../shared/types/product";
import axios from "axios";
import { fetchProductsByCategory } from "../../api/products";

const Shop: React.FC = () => {
  const { category } = useParams();
  console.log(category);
  const [products, setProducts] = useState<Product[]>([]);
  useEffect(() => {
    if (!category) return;
    fetchProductsByCategory(category)
      .then((res) => {
        setProducts(res.data);
      })
      .catch((err) => console.error("Failed to fetch products:", err));
  }, [category]);
  return (
    <div className="shop-page" style={{ padding: "2rem" }}>
      <h1>
        Shop{" "}
        {category
          ? category?.charAt(0).toUpperCase() + category?.substring(1)
          : "All"}
      </h1>
      <p>Browse our latest footwear collection!</p>
      {/* Product listing will go here */}
      <div className="products-grid">
        {/* Example placeholder products */}
        <div className="product-card">
          <img
            src="/placeholder-shoe.jpg"
            alt="Shoe"
            style={{ width: "100%" }}
          />
          <h2>Classic Sneaker</h2>
          <p>$79.99</p>
          <button>Add to Cart</button>
        </div>
        <div className="product-card">
          <img
            src="/placeholder-shoe2.jpg"
            alt="Shoe"
            style={{ width: "100%" }}
          />
          <h2>Sport Runner</h2>
          <p>$89.99</p>
          <button>Add to Cart</button>
        </div>
      </div>
    </div>
  );
};

export default Shop;
