import React from "react";
import { useCart } from "../../context/CartContext";
import ProductCartItem from "../ProductCartItem";
import { formatCurrency } from "../../utils";
import "./ShoppingCart.css";
import { SideBarContentProps } from "../SideBar/SideBar";
import { chevronRight, close } from "../../assets";

interface ShoppingCartProps extends SideBarContentProps {
  isCartOpen: boolean;
  onCheckoutPress: () => void;
}

const ShoppingCart: React.FC<ShoppingCartProps> = ({
  onClose,
  onCheckoutPress,
}) => {
  const { cartItems, totalPrice } = useCart();

  return (
    <div className="cart-container">
      <div className="cart-heading">
        <h2>Your Cart</h2>
        <button className="icon close-btn" onClick={onClose}>
          <img src={close} alt="Close" />
        </button>
      </div>
      <div className="content-wrap">
        {cartItems.length > 0 ? (
          cartItems.map((item) => <ProductCartItem key={item.id} item={item} />)
        ) : (
          <p className="content-centered">Nothing is in your cart yet.</p>
        )}
      </div>
      <div>
        <h3>
          Subtotal: <span>{formatCurrency(totalPrice)}</span>
        </h3>
      </div>
      <button
        style={{ display: "flex", alignItems: "center" }}
        onClick={onCheckoutPress}
        className={`button-primary ${
          cartItems.length === 0 ? "disabled" : ""
        } `}
      >
        Checkout
        <img className="icon" src={chevronRight} alt="Chevron Right" />
      </button>
    </div>
  );
};

export default ShoppingCart;
