import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { Product as ProductType } from "../../../../shared/types";
import { fetchProductsByCategory } from "../../api/shop";
import { ProductGridItem, SearchBar } from "../../components";
import { formatCategory } from "../../utils";
import "./Shop.css";
import NotFound from "../NotFound/NotFound";
const Shop: React.FC = () => {
  const { category = "All", subcategory } = useParams();
  const [products, setProducts] = useState<ProductType[]>([]);
  const [title, setTitle] = useState("");
  const navigate = useNavigate();
  const [notFound, setNotFound] = useState(false);
  useEffect(() => {
    // Build query parameters
    const params: Record<string, string> = { category };
    if (subcategory) params.subcategory = subcategory;
    setTitle(
      subcategory
        ? `${formatCategory(category).toUpperCase()} → ${formatCategory(
            subcategory
          )}`
        : `${formatCategory(category).toUpperCase()}`
    );

    fetchProductsByCategory(category)
      .then((res) => {
        console.log(res);
        setProducts(res);
      })
      .catch((err) => {
        if (err.response.status === 404) {
          setNotFound(true);
        }
        console.error("Error fetching products", err);
      });
  }, [category, subcategory, navigate]);

  if (notFound) {
    return <NotFound />;
  }

  return (
    <div className="shop-container">
      <SearchBar />

      <h1>Shop {title}</h1>
      {products.length > 0 ? (
        <div className="content-grid">
          {products.map((item) => (
            <ProductGridItem key={item.id} item={item} />
          ))}
        </div>
      ) : (
        <div className="content-centered">
          <p>Sorry, there are no items in this category.</p>
        </div>
      )}
    </div>
  );
};

export default Shop;
