import React, { cloneElement, useEffect } from "react";
import "./SideBar.css";
import FocusTrap from "../FocusTrap/FocusTrap";

export interface SideBarContentProps
  extends React.HTMLAttributes<HTMLDivElement> {
  onClose?: () => void;
  isOpen?: boolean;
}

interface SideBarProps {
  children: React.ReactElement<SideBarContentProps>;
  containerStyle?: React.CSSProperties;
  contentStyle?: React.CSSProperties;
  isOpen?: boolean;
  onClose?: () => void;
  openButton?: HTMLButtonElement;
  hidden?: boolean;
}
const SideBar: React.FC<SideBarProps> = ({
  children,
  containerStyle,
  contentStyle,
  hidden,
  onClose,
  openButton,
}) => {
  const [isOpen, setIsOpen] = React.useState(hidden);
  // const [isOpen, setIsOpen] = React.useState(false);
  // const { closeNav, isNavOpen } = useNav();
  const closeSidebar = React.useCallback(() => {
    if (openButton) {
      openButton.focus();
    }
    onClose && onClose();
  }, [onClose, openButton]);

  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.key === "Escape") {
        closeSidebar();
      }
    };
    document.addEventListener("keydown", handleKeyDown);
    return () => {
      document.removeEventListener("keydown", handleKeyDown);
    };
  }, [closeSidebar]);

  return (
    <FocusTrap active={isOpen} onClickOutside={closeSidebar}>
      <div
        style={containerStyle}
        className={`sidebar-container ${hidden ? "closed" : "open"}`}
      >
        <div style={contentStyle} className="sidebar-content">
          {cloneElement(children, {
            onClose: closeSidebar,
            isOpen,
          })}
        </div>
      </div>
    </FocusTrap>
  );
};

export default SideBar;
