import { useEffect } from "react";
import { useProductStore } from "../stores/useProductStore";

const useProduct = (id: number = 0) => {
  const category = useProductStore((s) => s.productCategories[id]);
  const subcategories = useProductStore((s) => s.productSubcategories[id]);
  const product = useProductStore((s) => s.products[id]);
  const variants = useProductStore((s) => s.productVariants[id]);

  const flags = useProductStore((s) => s.productFlags[id]);
  const brand = useProductStore((s) => s.productBrands);
  const images = useProductStore((s) => s.productImages[id]);
  const loading = useProductStore((s) => s.isLoading);
  const getProductCategory = useProductStore((s) => s.getProductCategory);
  const getProduct = useProductStore((s) => s.getProductById);

  const getProductSubcategories = useProductStore(
    (s) => s.getProductSubcategory
  );
  const getProductVariants = useProductStore((s) => s.getProductVariants);

  const getProductImages = useProductStore((s) => s.getProductImages);
  const getProductFlags = useProductStore((s) => s.getProductFlags);
  const getProductBrand = useProductStore((s) => s.getProductBrand);

  useEffect(() => {
    if (id) {
      getProduct(Number(id));

      getProductCategory(id);
      getProductImages(id);
      getProductFlags(id);
      getProductBrand(id);
      getProductSubcategories(id);
      getProductVariants(id);
    }
  }, [
    getProductSubcategories,
    getProductBrand,
    getProductCategory,
    getProductFlags,
    getProductImages,
    getProductVariants,
    getProduct,
    id,
  ]);

  return {
    product,
    variants,
    category,
    subcategories,
    flags,
    brand,
    images,
    loading,
  };
};

export default useProduct;
