import React, { useEffect, useState } from "react";
import { Product } from "../../../../../shared/types";
import { ProductGridItem, SearchBar } from "../../../components";
import { useNavigate } from "react-router-dom";
import {
  addProduct,
  deleteProduct,
  fetchProducts,
} from "../../../api/admin/products";

const Admin = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [error, setError] = useState<string | null>(null);
  const navigate = useNavigate();
  useEffect(() => {
    fetchProducts()
      .then((res) => {
        setProducts(res);
      })
      .catch((err) => {
        setError(err.message);
      });
  }, []);
  const handleDelete = (id: number) => {
    deleteProduct(id)
      .then(() => {
        setProducts((prev) => prev.filter((item) => item.id !== id));
      })
      .catch((err) => {
        setError(err.message);
      });
  };
  const handleAddClick = () => {
    addProduct().catch((err) => {
      setError(err.message);
    });
  };
  function handleEditClick(id: number): void {
    navigate(`/admin/product/${id}`);
  }
  if (error) {
    <div className="content-centered">
      <p>{error}</p>
    </div>;
  }
  return (
    <div className="admin-container">
      <SearchBar />
      <h1>All Products</h1>
      <button onClick={handleAddClick} className="button-primary">
        Add Product
      </button>
      <div className="content-body content-grid">
        {products.map((item) => (
          <div>
            <ProductGridItem item={item} />
            <div className="flex-content">
              <button
                className="button-outlined"
                onClick={() => handleEditClick(item.id)}
              >
                Edit Product
              </button>
              <button
                className="button-danger"
                onClick={() => handleDelete(item.id)}
              >
                Delete Product
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Admin;
