import React, { useEffect, useState } from "react";

interface UseAnimationToggleProps {
  animate: boolean;
  setToggle: React.Dispatch<React.SetStateAction<boolean>>;
  duration?: number;
}
const useAnimationToggle = ({
  animate,
  setToggle,
  duration,
}: UseAnimationToggleProps) => {
  const [isAnimating, setIsAnimating] = useState(false);
  useEffect(() => {
    if (animate) {
      setTimeout(() => {
        setIsAnimating(true);
      }, duration || 300);
    } else {
      setTimeout(() => {
        setIsAnimating(true);
      }, duration || 300);
    }
  }, [animate, setToggle, duration]);
  return [isAnimating] as const;
};
export default useAnimationToggle;
