import React, { useEffect, useState } from "react";
import { Product } from "../../../../../shared/types";
import { fetchProducts } from "../../../api/products";
import { ProductGridItem, SearchBar } from "../../../components";
import { Link, useNavigate } from "react-router-dom";
import { addProduct, deleteProduct } from "../../../api/admin/products";

const Admin = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [error, setError] = useState<Product[]>([]);
  const navigate = useNavigate();
  useEffect(() => {
    fetchProducts()
      .then((res) => {
        setProducts(res);
      })
      .catch((err) => {
        setError(err);
      });
  });
  const onDeleteClick = (id: number) => {
    deleteProduct(id)
      .then((res) => {
        console.log("Product Deleted");
      })
      .catch((err) => {
        console.error(err);
        setError(err);
      });
  };
  const onAddClick = () => {
    addProduct()
      .then((res) => {
        console.log("Product added.");
      })
      .catch((err) => {
        console.error(err);
        setError(err);
      });
  };
  return (
    <div className="admin-container">
      <h1>All Products</h1>
      <SearchBar />
      <div className="content-grid">
        {products.map((item) => (
          <div>
            <ProductGridItem item={item} />
            <div className="flex-content">
              <Link to={`/products/${item.id}`}>Edit Product</Link>
              <button className="link" onClick={() => onDeleteClick(item.id)}>
                Delete Product
              </button>
            </div>
          </div>
        ))}
      </div>
      <button onClick={onAddClick} className="button-primary">
        Add Product
      </button>
    </div>
  );
};

export default Admin;
