import { ProductType } from "../../../shared/types/product";
import api from "./axios";
export const fetchProductsByCategory = async (
  category: string,
  subcategory?: string
): Promise<ProductType[]> => {
  if (subcategory) {
    const response = await api.get(`/shop/category`, {
      params: { category, subcategory },
    });
    return response.data;
  }
  const response = await api.get(`/shop/category`, { params: { category } });
  return response.data;
};
export const fetchProducts = async () => {
  const response = await api.get(`/shop`);
  return response.data;
};

export const fetchProductsBySearch = async (query: string) => {
  const response = await api.get(`/search`, { params: { query } });
  return response.data;
};
export const fetchProduct = async (id: number): Promise<ProductType> => {
  const response = await api.get(`/product`, { params: { id } });
  return response.data;
};
