import React, { useEffect, useState } from "react";
import { Brand, Category, Product } from "../../../../../shared/types";
import { useParams } from "react-router-dom";
import { fetchProduct, updateProduct } from "../../../api/admin/products";
import {
  fetchBrands,
  fetchCategories,
  fetchSubcategories,
} from "../../../api/tags";
import "./EditProduct.css";
const EditProduct = () => {
  const [product, setProduct] = useState<Product | null>(null);
  const [categories, setCategories] = useState<Category[]>([]);
  const [subcategories, setSubcategories] = useState<Category[]>([]);
  const [brands, setBrands] = useState<Brand[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const { id } = useParams();
  const [inputs, setInputs] = useState({
    name: "",
    price: 0,
    description: "",
    brand_id: 0,
    subcategory_id: 0,
    category_id: 0,
    discount: 0,
    stock_quantity: 0,
    created_at: "",
  });
  useEffect(() => {
    if (!product) return;
    setInputs({
      ...product,
    });
  }, [product]);
  useEffect(() => {
    if (!id) {
      return;
    }
    setLoading(true);

    fetchCategories()
      .then((res) => {
        setCategories(res);
      })
      .catch((err) => {
        setError(err.message);
      });
    fetchSubcategories()
      .then((res) => {
        setSubcategories(res);
      })
      .catch((err) => {
        setError(err.message);
      });
    fetchBrands()
      .then((res) => {
        setBrands(res);
      })
      .catch((err) => {
        setError(err.message);
      });
    fetchProduct(parseInt(id))
      .then((res) => {
        setProduct(res);
        setLoading(false);
      })
      .catch((err) => {
        setLoading(false);

        setError(err.message);
      });
  }, [id]);
  if (loading) {
    <div className="content-centered">Loading...</div>;
  }
  if (error) {
    return (
      <div className="content-centered">
        <h1>Uh oh, an error occured.</h1>
        {error && <p>{error}</p>}
      </div>
    );
  }
  if (!product) {
    return (
      <div className="content-centered">
        <h1>Uh oh, we coudn't find this product.</h1>

        {error && <p>{error}</p>}
      </div>
    );
  }

  const handleChange = (
    e: React.ChangeEvent<
      HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement
    >
  ) => {
    console.log({ ...inputs, [e.target.name]: e.target.value });
    setInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };
  const handleSubmit = () => {
    updateProduct(product.id, inputs)
      .then(() => {
        console.log("Product updated");
      })
      .catch((err) => {
        setError(err.message);
      });
  };
  return (
    <div className="product-container">
      <h1>Edit Product</h1>
      <p>Product ID: {product.id}</p>
      <div className="content-body">
        <form method="put" onSubmit={handleSubmit}>
          <div className="flex-content align-end">
            <div>
              <label htmlFor="name">Name</label>

              <input
                style={{ width: "100%" }}
                onChange={handleChange}
                className="text-input"
                id="name"
                name="name"
                type="text"
                defaultValue={inputs.name}
              />
            </div>
          </div>
          <div>
            <div className="flex-content align-end">
              <div style={{ flex: 1, width: "100%" }} className="content-row">
                <div>
                  <label htmlFor="category">Category</label>

                  <select
                    style={{ width: "100%" }}
                    onChange={handleChange}
                    className="text-input"
                    id="category"
                    name="category_id"
                    value={inputs.category_id}
                  >
                    {categories.map((item) => (
                      <option value={item.id} key={item.id}>
                        {item.name}
                      </option>
                    ))}
                  </select>
                </div>

                <div>
                  <label htmlFor="subcategory">Subcategory</label>

                  <select
                    style={{ width: "100%" }}
                    onChange={handleChange}
                    className="text-input"
                    id="subcategory"
                    name="subcategory_id"
                    value={inputs.subcategory_id}
                  >
                    {subcategories.map((item) => (
                      <option value={item.id} key={item.id}>
                        {item.name}
                      </option>
                    ))}
                  </select>
                </div>
              </div>
            </div>
          </div>
          <div>
            <div className="flex-content align-end">
              <div>
                <label htmlFor="brand">Brand</label>

                <select
                  style={{ width: "100%" }}
                  onChange={handleChange}
                  className="text-input"
                  id="brand"
                  name="brand_id"
                  value={inputs.brand_id}
                >
                  {brands.map((item) => (
                    <option value={item.id} key={item.id}>
                      {item.name}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          </div>
          <div>
            <div className="flex-content align-end">
              <div>
                <label htmlFor="price">Price</label>
                <input
                  onChange={handleChange}
                  className="text-input"
                  type="number"
                  step=".01"
                  name="price"
                  id="price"
                  defaultValue={product.price}
                />
              </div>

              <div>
                <label htmlFor="discount">Discount</label>
                <input
                  onChange={handleChange}
                  step=".01"
                  className="text-input"
                  type="number"
                  name="discount"
                  id="discount"
                  defaultValue={product.discount}
                />
              </div>
              <div>
                <label htmlFor="stock-quantity">Stock Quantity</label>
                <input
                  onChange={handleChange}
                  className="text-input"
                  type="number"
                  name="stock_quantity"
                  id="stock-quantity"
                  defaultValue={product.stock_quantity}
                />
              </div>
            </div>
          </div>

          <div>
            <div className="flex-content align-end">
              <div>
                <label htmlFor="description">Description</label>
                <textarea
                  style={{ width: "100%" }}
                  className="text-input"
                  name="description"
                  id="description"
                  onChange={handleChange}
                  defaultValue={product.description}
                />
              </div>
            </div>
          </div>
          <button type="submit" className="button-primary">
            Update
          </button>
        </form>
      </div>
    </div>
  );
};

export default EditProduct;
