import React, { useEffect, useState } from "react";
import {
  Brand,
  Category,
  Product,
  ProductImage,
} from "../../../../../shared/types";
import { useParams } from "react-router-dom";
import {
  deleteImage,
  fetchProduct,
  updateProduct,
  fetchProductImages,
} from "../../../api/admin/products";
import {
  fetchBrands,
  fetchCategories,
  fetchSubcategories,
} from "../../../api/tags";
import "./EditProduct.css";
import { add } from "../../../assets";
const EditProduct = () => {
  const [product, setProduct] = useState<Product | null>(null);
  const [categories, setCategories] = useState<Category[]>([]);
  const [subcategories, setSubcategories] = useState<Category[]>([]);
  const [images, setImages] = useState<ProductImage[]>([]);
  const [newImages, setNewImages] = useState<ProductImage[]>([]);

  const [brands, setBrands] = useState<Brand[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const { id } = useParams();
  const [inputs, setInputs] = useState({
    name: "",
    price: 0,
    description: "",
    brand_id: 0,
    subcategory_id: 0,
    images: [],
    category_id: 0,
    discount: 0,
    stock_quantity: 0,
    created_at: "",
  });

  useEffect(() => {
    if (!id) {
      return;
    }
    const productId = parseInt(id);
    setLoading(true);
    fetchProductImages(productId)
      .then((res) => {
        setImages(res);
      })
      .catch((err) => {
        setError(err.message);
      });

    fetchCategories()
      .then((res) => {
        setCategories(res);
      })
      .catch((err) => {
        setError(err.message);
      });
    fetchSubcategories()
      .then((res) => {
        setSubcategories(res);
      })
      .catch((err) => {
        setError(err.message);
      });
    fetchBrands()
      .then((res) => {
        setBrands(res);
      })
      .catch((err) => {
        setError(err.message);
      });
    fetchProduct(productId)
      .then((res) => {
        setProduct(res);
        setInputs({ ...res, images: [] });
        setLoading(false);
      })
      .catch((err) => {
        setLoading(false);

        setError(err.message);
      });
  }, [id]);
  if (loading) {
    <div className="content-centered">Loading...</div>;
  }
  if (error) {
    return (
      <div className="content-centered">
        <h1>Uh oh, an error occured.</h1>
        {error && <p>{error}</p>}
      </div>
    );
  }
  if (!product) {
    return (
      <div className="content-centered">
        <h1>Uh oh, we coudn't find this product.</h1>

        {error && <p>{error}</p>}
      </div>
    );
  }

  const handleChange = (
    e: React.ChangeEvent<
      HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement
    >
  ) => {
    console.log({ ...inputs, [e.target.name]: e.target.value });
    setInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleImageChange = (
    index: number,
    image_url: string,
    alt_text: string
  ) => {
    const image = (newImages[index] = {
      ...newImages[index],
      image_url,
      alt_text,
      product_id: product.id,
    });
    setNewImages((prev) => [...prev, image]);
  };
  const handleSubmit = () => {
    updateProduct(product.id, inputs)
      .then(() => {
        console.log("Product updated");
      })
      .catch((err) => {
        setError(err.message);
      });
  };
  const handleDeleteImage = (imageId: number) => {
    deleteImage(product.id, imageId).catch((err) => {
      setError(err.message);
    });
  };

  const handleAddImage = (image_url: string, alt_text: string) => {
    setNewImages((prev) => [
      ...prev,
      { image_url, alt_text, product_id: product.id },
    ]);
  };

  return (
    <div className="product-container">
      <h1>Edit Product</h1>
      <p>Product ID: {product.id}</p>
      <div className="content-body">
        <form method="put" onSubmit={handleSubmit}>
          <div className="flex-content">
            <div>
              <label htmlFor="name">Name</label>

              <input
                onChange={handleChange}
                className="text-input"
                id="name"
                name="name"
                type="text"
                value={inputs.name}
              />
            </div>
          </div>
          <div className="flex-content">
            <div>
              <label htmlFor="category">Category</label>

              <select
                onChange={handleChange}
                className="text-input"
                id="category"
                name="category_id"
                value={inputs.category_id}
              >
                <option value={0}>Select Option</option>

                {categories.map((item) => (
                  <option value={item.id} key={item.id}>
                    {item.name}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label htmlFor="subcategory">Subcategory</label>

              <select
                onChange={handleChange}
                className="text-input"
                id="subcategory"
                name="subcategory_id"
                value={inputs.subcategory_id}
              >
                <option value={0}>Select Option</option>
                {subcategories.map((item) => (
                  <option value={item.id} key={item.id}>
                    {item.name}
                  </option>
                ))}
              </select>
            </div>
            <div>
              <label htmlFor="brand">Brand</label>

              <select
                onChange={handleChange}
                className="text-input"
                id="brand"
                name="brand_id"
                value={inputs.brand_id}
              >
                <option value={0}>Select Option</option>

                {brands.map((item) => (
                  <option value={item.id} key={item.id}>
                    {item.name}
                  </option>
                ))}
              </select>
            </div>
          </div>
          <div className="flex-content">
            <div>
              <label htmlFor="price">Price</label>
              <input
                onChange={handleChange}
                className="text-input"
                type="number"
                step=".01"
                name="price"
                id="price"
                defaultValue={inputs.price}
              />
            </div>

            <div>
              <label htmlFor="discount">Discount</label>
              <input
                onChange={handleChange}
                step=".01"
                className="text-input"
                type="number"
                name="discount"
                id="discount"
                defaultValue={inputs.discount}
              />
            </div>
            <div>
              <label htmlFor="stock-quantity">Stock Quantity</label>
              <input
                onChange={handleChange}
                className="text-input"
                type="number"
                name="stock_quantity"
                id="stock-quantity"
                value={inputs.stock_quantity}
              />
            </div>
          </div>

          <div className="flex-content">
            <div>
              <label htmlFor="description">Description</label>
              <textarea
                className="text-input"
                name="description"
                id="description"
                onChange={handleChange}
                value={inputs.description}
              />
            </div>
          </div>
          <form onSubmit={(e) => e.preventDefault()} className="content-row">
            <div className="flex-content">
              <button
                onClick={() =>
                  setNewImages((prev) => [
                    ...prev,
                    { image_url: "", alt_text: "", product_id: product.id },
                  ])
                }
                className="button-circle"
              >
                <img className="icon" src={add} alt="Add" />
              </button>
              {images.map((item) => (
                <div>
                  <label htmlFor="images">Images</label>
                  <input
                    type="text"
                    className="text-input"
                    name="image"
                    id="image"
                    onChange={() => handleAddImage(index)}
                    value={item.image_url}
                  />
                  <div>
                    <button onClick={() => handleDeleteImage} className="link">
                      Delete
                    </button>
                  </div>
                </div>
              ))}
            </div>

            <div className="scroll-content">
              {newImages.map((item) => (
                <div>
                  <div>
                    <label htmlFor="images">Images</label>
                    <input
                      type="text"
                      className="text-input"
                      name="images"
                      id="images"
                      onChange={handleChange}
                      value={item.image_url}
                    />
                  </div>
                  <div>
                    <label htmlFor="images">Images</label>
                    <input
                      type="text"
                      className="text-input"
                      name="images"
                      id="images"
                      onChange={handleChange}
                      value={item.image_url}
                    />
                  </div>

                  <button onClick={() => handleDeleteImage} className="link">
                    Remove
                  </button>
                </div>
              ))}
            </div>
          </form>
          <div className="flex-content">
            <button
              value={"update"}
              type="submit"
              className="submit button-primary"
            >
              Update
            </button>
            <button
              value="delete"
              type="submit"
              className="submit button-primary"
            >
              Delete
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditProduct;
