import React, { useEffect, useRef } from "react";
import "./SideBar.css";
import { FocusTrap } from "focus-trap-react";
import { useAnimationToggle } from "../../hooks";

export interface SideBarContentProps
  extends React.HTMLAttributes<HTMLDivElement> {
  onClose?: () => void;
  isOpen?: boolean;
}

interface SideBarProps {
  children: React.ReactElement<SideBarContentProps>;
  containerStyle?: React.CSSProperties;
  contentStyle?: React.CSSProperties;
  isOpen: boolean;
  onClose?: () => void;
  onOpen?: () => void;
  openButton: React.RefObject<HTMLButtonElement> | null;
}
const SideBar: React.FC<SideBarProps> = ({
  children,
  containerStyle,
  contentStyle,
  isOpen,
  onClose,
  openButton,
}) => {
  const closeSidebar = React.useCallback(() => {
    openButton?.current.focus();
    console.log(openButton);
    onClose && onClose();
  }, [onClose, openButton]);
  const ref = useRef<HTMLDivElement>(null);

  // useEffect(() => {
  //   if (!isOpen) {
  //     closeSidebar();
  //   } else {
  //     if (ref.current) ref.current.hidden = false;
  //   }
  // }, [closeSidebar, isOpen]);
  useEffect(() => {
    if (!isOpen) {
      return;
    }
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.key === "Escape") {
        closeSidebar();
      }
    };
    document.addEventListener("keydown", handleKeyDown);
    return () => {
      document.removeEventListener("keydown", handleKeyDown);
    };
  }, [closeSidebar, isOpen]);
  return (
    <FocusTrap
      focusTrapOptions={{
        clickOutsideDeactivates: true,
        returnFocusOnDeactivate: true,
        onDeactivate: () => {
          if (ref.current) ref.current.hidden = true;
        },
      }}
      active={isOpen}
    >
      <div
        ref={ref}
        // hidden={!endValue}
        style={containerStyle}
        className={`sidebar-container ${isOpen ? "open" : "closed"}`}
      >
        <div style={contentStyle} className="sidebar-content">
          {React.cloneElement(children, {
            isOpen,
            onClose: closeSidebar,
          })}
        </div>
      </div>
    </FocusTrap>
  );
};

export default SideBar;
