import { Product, ProductFlag, ProductImage } from "../../../shared/types";
import api from "./axios";

export const fetchProduct = async (id: number): Promise<Product> => {
  const response = await api.get(`/products/${id}`);
  return response.data;
};

export const fetchProductImages = async (
  id: number
): Promise<ProductImage[]> => {
  const response = await api.get(`/products/${id}/image/images`);
  return response.data;
};

export const fetchProductFlags = async (id: number): Promise<ProductFlag[]> => {
  const response = await api.get(`/products/${id}/flags`);
  return response.data;
};
