import React, { useEffect, useState } from "react";
import { Category, Product as ProductType } from "../../../../../shared/types";
import { useParams } from "react-router-dom";
import { fetchProduct, updateProduct } from "../../../api/admin/products";
import { fetchCategories, fetchSubcategories } from "../../../api/tags";
import "./Product.css";
const Product = () => {
  const [product, setProduct] = useState<ProductType | null>(null);
  const [categories, setCategories] = useState<Category[]>([]);
  const [subcategories, setSubcategories] = useState<Category[]>([]);

  const [error, setError] = useState<string | null>(null);
  const { id } = useParams();
  const [inputs, setInputs] = useState({
    name: null,
    price: null,
    description: null,
    brandId: null,
    subcategoryId: null,
    categoryId: null,
    discount: null,
    stockQuantity: null,
  });

  useEffect(() => {
    if (!id) {
      return;
    }
    fetchCategories()
      .then((res) => {
        setCategories(res);
      })
      .catch((err) => {
        console.error(err);
        setError("Error fetching categories: " + err.message);
      });
    fetchSubcategories()
      .then((res) => {
        console.log({ res });
        setSubcategories(res);
      })
      .catch((err) => {
        console.error(err);
        setError("Error fetching categories: " + err.message);
      });
    fetchProduct(parseInt(id))
      .then((res) => {
        setProduct(res);
      })
      .catch((err) => {
        console.error(err);
        setError("Error fetching product: " + err.message);
      });
  }, [id]);
  useEffect(() => {
    if (!product) return;
  }, [inputs, product]);
  if (error) {
    return (
      <div className="content-centered">
        <h1>Uh oh, an error occured.</h1>
        {error && <p>{error}</p>}
      </div>
    );
  }
  if (!product) {
    return (
      <div className="content-centered">
        <h1>Uh oh, we coudn't find this product.</h1>

        {error && <p>{error}</p>}
      </div>
    );
  }

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>
  ) => {
    setInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };
  const onSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    updateProduct(product.id, inputs)
      .then(() => {
        console.log("Product updated");
      })
      .catch((err) => {
        setError(err.message);
      });
  };
  return (
    <div>
      <h1>{product.name}</h1>
      <div className="content-body">
        <form method="put" onSubmit={onSubmit}>
          <div>
            <label htmlFor="name">Name</label>

            <div className="flex-content">
              <input
                onChange={handleChange}
                className="text-input"
                id="name"
                name="name"
                type="text"
                defaultValue={product.name}
              />
              <button type="submit" className="button-primary">
                Update
              </button>
            </div>
          </div>
        </form>
        <form method="put" onSubmit={onSubmit}>
          <div className="flex-content align-end">
            <div>
              <label htmlFor="name">Category</label>

              <select
                style={{ width: "100%" }}
                onChange={handleChange}
                className="text-input"
                id="categories"
                name="categoryId"
                defaultValue={product.category_id}
              >
                {categories.map((item) => (
                  <option value={item.id} key={item.id}>
                    {item.name}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label htmlFor="name">Subcategory</label>

              <select
                style={{ width: "100%" }}
                onChange={handleChange}
                className="text-input"
                id="subcategories"
                name="subcategoryId"
                defaultValue={product.subcategory_id}
              >
                {subcategories.map((item) => (
                  <option value={item.id} key={item.id}>
                    {item.name}
                  </option>
                ))}
              </select>
            </div>
            <button type="submit" className="button-primary">
              Update
            </button>
          </div>
        </form>

        <form method="put" onSubmit={onSubmit}>
          <div className="flex-content align-end">
            <div>
              <label htmlFor="price">Price</label>
              <input
                onChange={handleChange}
                className="text-input"
                type="number"
                step=".01"
                name="price"
                id="price"
                defaultValue={product.price}
              />
            </div>

            <div>
              <label htmlFor="discount">Discount</label>
              <input
                onChange={handleChange}
                step=".01"
                className="text-input"
                type="number"
                name="discount"
                id="discount"
                defaultValue={product.discount}
              />
            </div>
            <div>
              <label htmlFor="stock-quantity">Stock Quantity</label>
              <input
                onChange={handleChange}
                className="text-input"
                type="number"
                name="stockQuantity"
                id="stock-quantity"
                defaultValue={product.stockQuantity}
              />
            </div>
            <button type="submit" className="button-primary">
              Update
            </button>
          </div>
        </form>

        <form method="put" onSubmit={onSubmit}>
          <label htmlFor="description">Description</label>
          <textarea
            name="description"
            id="description"
            onChange={handleChange}
          />
          <button type="submit" className="button-primary">
            Update
          </button>
        </form>
      </div>
    </div>
  );
};

export default Product;
