import React from "react";
import maleImage from "../../assets/images/male-shoes.jpeg";
import femaleImage from "../../assets/images/female-shoes.jpeg";
import arrow from "../../assets/images/right-arrow.png";
import "./Category.css";
import { Link, NavLinkRenderProps } from "react-router-dom";


interface CategoryProps{
  type: "men" | "women";
}

const Category = ( {type} : CategoryProps)  => {
  return (
    <Link to={"/shop"} state={{type}} className="category-box content-row">
      {type === "men" ? (
        <img src={maleImage} alt="male sitting down" />
      ) : (
        <img src={femaleImage} alt="female sitting down" />
      )}
      <div className="overlay">
        {type === "men" ? <h2>MEN</h2> : <h2>WOMEN</h2>}
        <p className="shop-now">
          <span>Shop Now</span>
          <img src={arrow} alt="right arrow" />
        </p>
      </div>
    </Link>
  );
}

export default Category;
