import React from "react";
import { Link } from "react-router-dom";
import "./ProductGridItem.css";
import { Product } from "../../../../shared/types";
import "../../styles/shared/_product.css";
import { formatCategory, formatCurrency } from "../../utils";
import { useProductStore } from "../../stores/useProductStore";

function ProductGridItem({ item }: { item: Product }) {
  const category = useProductStore((s) => s.productCategories[item.id]);
  const flags = useProductStore((s) => s.productFlags[item.id]);
  const images = useProductStore((s) => s.productImages[item.id]);
  const loading = useProductStore((s) => s.isLoading);
  if (loading) {
    return null;
  }

  return (
    <Link to={`/product/${item.id}`} state={item} className="product-grid-item">
      <div className="product-top">
        <div className="badge-container">
          {flags.some((item) => item.name === "new") && (
            <span className="new-badge">NEW</span>
          )}
          {item.discount != null && <span className="sale-badge">SALE</span>}
        </div>

        <img src={images[0].image_url} alt={images[0].alt_text} />
      </div>

      <div className="product-bottom">
        <h3>{item.name}</h3>
        <p className="category">{formatCategory(category.name)}</p>

        {item.discount ? (
          <p className="price">
            {" "}
            <span className="old-price">
              {formatCurrency(item.price)}{" "}
            </span>{" "}
            <span className="sale-price">
              {formatCurrency(item.price * item.discount)}
            </span>
          </p>
        ) : (
          <p className="price">{formatCurrency(item.price)}</p>
        )}

        {/* {item.reviews.length > 0 && (
          <div className="stars">
            {Array(
              Math.round(
                item.reviews.reduce((stars, item) => item.stars + stars, 0) /
                  item.reviews.length
              )
            )
              .fill("")
              .map((_, index) => (
                <span key={index}>&#9733;</span>
              ))}
          </div>
        )} */}
      </div>
    </Link>
  );
}

export default ProductGridItem;
