import { Product, ProductImage } from "../../../../shared/types";
import api from "../axios";

export const deleteProduct = async (id: number) => {
  const response = await api.delete(`/admin/products/${id}`);
  return response.data;
};
export const addProduct = async () => {
  const response = await api.post(`/admin`);
  return response.data;
};

export const fetchProducts = async () => {
  const response = await api.get(`/admin/products`);
  return response.data;
};

export const fetchProduct = async (id: number): Promise<Product> => {
  const response = await api.get(`/admin/products/${id}`);
  return response.data;
};

export const updateProduct = async (
  id: number,
  body: any
): Promise<Product> => {
  const response = await api.put(`/admin/products/${id}`, body);
  return response.data;
};

export const deleteImage = async (
  id: number,
  imageId: number
): Promise<Product> => {
  const response = await api.delete(`/admin/products/${id}/image/${imageId}`);
  return response.data;
};

export const fetchProductImages = async (
  id: number
): Promise<ProductImage[]> => {
  const response = await api.get(`/admin/products/${id}/images`);
  return response.data;
};
