import React, { useEffect, useState } from "react";
import { Product } from "../../../../../shared/types";
import { useParams } from "react-router-dom";
import { fetchProduct, updateProduct } from "../../../api/admin/products";
import { ProductItem } from "../../../components";

const ProductPanel = () => {
  const [product, setProduct] = useState<Product | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [images, setImages] = useState<string[]>([]);
  const { id } = useParams();
  const [inputs, setInputs] = useState({
    name: null,
    price: null,
    description: null,
    brandId: null,
    subcategoryId: null,
    categoryId: null,
    discount: null,
    stockQuantity: null,
  });

  useEffect(() => {
    if (!id) {
      return;
    }
    fetchProduct(parseInt(id))
      .then((res) => {
        setProduct(res);
      })
      .catch((err) => {
        console.error(err);
        setError(err.message);
      });
  }, [id]);
  useEffect(() => {
    if (!product) return;
    updateProduct(product.id, inputs);
  }, [inputs]);
  if (error) {
    return (
      <div className="content-centered">
        <h1>Uh oh, an error occured.</h1>
      </div>
    );
  }
  if (!product) {
    return (
      <div className="content-centered">
        <h1>Uh oh, we coudn't find this product.</h1>

        {error && <p>{error}</p>}
      </div>
    );
  }

  const onSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    setInputs();
  };
  return (
    <div>
      <h1>{product.name}</h1>
      <div className="content-body">
        <form method="put" onSubmit={onSubmit}>
          <div>
            <label htmlFor="image-url">Product Name</label>

            <div className="flex-content">
              <input
                className="text-input"
                id="image-url"
                type="text"
                defaultValue={product.name}
              />
              <button type="submit" className="button-primary">
                Update
              </button>
            </div>
          </div>
        </form>
        <form>
          {product.images.length > 0 && (
            <div>
              <label htmlFor="image-url">Images</label>
              <div className="content-row scroll-content">
                {product.images.map((item) => (
                  <div>
                    <input
                      className="text-input"
                      id="image-url"
                      type="text"
                      value={item}
                    />
                    <button className="button-outlined">Delete Image</button>
                  </div>
                ))}
              </div>
            </div>
          )}
        </form>
      </div>
    </div>
  );
};

export default ProductPanel;
