// useUIStore.ts
import { create } from "zustand";
import { CartItem } from "../../../../shared/types";

interface UIStore {
  toastItem: CartItem | null;
  showCartToast: (item: CartItem) => void;
  hideCartToast: () => void;
}

export const useUIStore = create<UIStore>((set) => ({
  toastItem: null,
  showCartToast: (item) => {
    set({ toastItem: item });
    setTimeout(() => set({ toastItem: null }), 3000); // auto-hide
  },
  hideCartToast: () => set({ toastItem: null }),
}));
