import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useNav } from "../../context";
import "./Header.css";
import Navigation from "../Navigation";
import { heart, logo, menu } from "../../assets";
import { useMediaQuery } from "@react-hook/media-query";
import SearchBar from "../SearchBar";
import { CartButton, MenuButton } from "../buttons/";
function Header() {
  const { isNavOpen, openNav } = useNav();
  const [showMenu, setShowMenu] = useState(false);

  const isDesktop = useMediaQuery("screen and (min-width: 720px)");
  // useEffect(() => {
  //   if (isDesktop) {
  //     closeNav();
  //   }
  // }, [isDesktop, closeNav]);

  const onNavClose = () => {
    const menuBtn = document.getElementById("menu-btn");
    menuBtn?.focus();
  };
  useEffect(() => {
    const handleScroll = () => {
      const header = document.querySelector("header");
      const headerHeight = header
        ? header.getBoundingClientRect().height + 100
        : 80;
      setShowMenu(window.scrollY > headerHeight);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <header>
      <div className="header-container">
        <div className="header-left">
          <Link
            tabIndex={showMenu ? -1 : undefined}
            aria-hidden={showMenu}
            to={"/"}
          >
            {" "}
            <img className="logo" src={logo} alt="logo" />
          </Link>
          <div style={{ display: "flex" }}>
            <button
              tabIndex={showMenu ? -1 : undefined}
              aria-hidden={showMenu}
              id="sign-up"
            >
              Sign Up
            </button>
            <button
              tabIndex={showMenu ? -1 : undefined}
              aria-hidden={showMenu}
              id="log-in"
            >
              Log In
            </button>
          </div>
        </div>

        <div className="header-right">
          <MenuButton
            disabled={showMenu}
            aria-controls="nav"
            aria-expanded={!isDesktop ? isNavOpen : undefined}
            className="menu-btn"
            id={showMenu ? "menu-btn" : undefined}
            onClick={openNav}
          />

          <SearchBar
            tabIndex={showMenu ? -1 : undefined}
            aria-hidden={showMenu}
          />
          <Link
            tabIndex={showMenu ? -1 : undefined}
            aria-hidden={showMenu}
            to={"/favorites"}
          >
            <img className="heart" src={heart} alt="heart" />
          </Link>
          <CartButton />
        </div>
      </div>
      <Navigation disabled={showMenu && !isNavOpen} onClose={onNavClose} />

      <div
        className={`sticky-header
        ${showMenu ? "open" : "closed"}
         ${showMenu && !isNavOpen ? "visible" : "hidden"}
          `}
      >
        <button
          aria-controls="nav"
          aria-expanded={!isDesktop ? isNavOpen : undefined}
          className="menu-btn"
          id={showMenu ? "menu-btn" : undefined}
          onClick={openNav}
        >
          <img className="icon" src={menu} alt="menu" />
        </button>
        <CartButton />
      </div>
    </header>
  );
}

export default Header;
