import React from "react";
import { NavLink } from "react-router-dom";
import "./Navigation.css";
import { useNav } from "../../context";
import { logo, close } from "../../assets";
import SearchBar from "../SearchBar";

interface NavigationProps {
  onClose?: () => void;
}
const Navigation = ({ onClose }: NavigationProps) => {
  const { closeNav, isNavOpen } = useNav();
  const onCloseClick = () => {
    closeNav();
    onClose && onClose();
  };
  return (
    <nav id="nav" className="closed">
      {isNavOpen && (
        <div className="nav-top">
          <img className="logo" src={logo} alt="Logo" />
          <SearchBar />
          <button autoFocus onClick={onCloseClick}>
            <img
              className="icon close-btn"
              src={close}
              alt="Close"
              onClick={closeNav}
            />
          </button>
        </div>
      )}
      <ul>
        <li>
          <NavLink to="/">
            {({ isActive }) => (
              <span className={isActive ? "active" : ""}>Home</span>
            )}
          </NavLink>
        </li>
        <li>
          <NavLink to="/shop/category//men">
            {({ isActive }) => (
              <span className={isActive ? "active" : ""}>Men</span>
            )}
          </NavLink>
        </li>
        <li>
          <NavLink to="/shop/category/women">
            {({ isActive }) => (
              <span className={isActive ? "active" : ""}>Women</span>
            )}
          </NavLink>
        </li>
        <li>
          <NavLink to={"/new-arrivals"}>
            {({ isActive }) => (
              <span className={isActive ? "active" : ""}>New Arrivals</span>
            )}
          </NavLink>
        </li>
        <li>
          <NavLink to={"/kids"}>
            {({ isActive }) => (
              <span className={isActive ? "active" : ""}>Kids</span>
            )}
          </NavLink>
        </li>
        <li>
          <NavLink to={"/releases"}>
            {({ isActive }) => (
              <span className={isActive ? "active" : ""}>Releases</span>
            )}
          </NavLink>
        </li>
      </ul>
    </nav>
  );
};

export default Navigation;
