import React from "react";
import { formatCurrency } from "../../utils";
import "./ProductCartItem.css";
import { CartItem } from "../../../../shared/types";
import { useCartStore } from "../../stores";

function ProductCartItem({ product }: { product: CartItem }) {
  const { removeItem } = useCartStore();

  return (
    <div className="product-cart-item">
      <div className="product-top">
        <img
          alt={product.images[0].altText || product.name}
          src={product.images[0].imageUrl}
        />
      </div>

      <div className="product-bottom">
        <h3>{product.name}</h3>

        <div className="details">
          <h5>Price</h5>
          {product?.discount ? (
            <p style={{ fontFamily: "Dunbar" }} className="price">
              {formatCurrency(product.discount)}
            </p>
          ) : (
            <p style={{ fontFamily: "Dunbar" }} className="price">
              {formatCurrency(product.price)}
            </p>
          )}
          <h5>Qty</h5>
          <div className="quantity">
            <p style={{ fontFamily: "Dunbar" }}>{product.quantity}</p>

            {/* <button
              onClick={() => item.quantity > 1 && decreaseCartQuantity(item)}
            >
              -
            </button> */}
            {/* <button onClick={() => increaseCartQuantity(item)}>+</button> */}
          </div>
          <h5>Size</h5>
          <p style={{ fontFamily: "Dunbar" }} className="size">
            {" "}
            {product.size} {product.category}
          </p>

          <h5>Color</h5>
          <div
            className="color-box"
            style={{ backgroundColor: product.color }}
          />
        </div>
        <div className="content-row">
          <button onClick={() => {}} className="link">
            Edit
          </button>

          <button
            onClick={() => removeItem(product.id)}
            className="link danger"
          >
            Remove
          </button>
        </div>
      </div>
    </div>
  );
}

export default ProductCartItem;
