import React, { useEffect, useState } from "react";
import logo from "../../assets/images/logo.png";
import heart from "../../assets/images/heart.png";
import cartImg from "../../assets/images/cart.png";
import { Link } from "react-router-dom";
import { useCart, useNav } from "../../context";
import "./Header.css";
import Navigation from "../Navigation";
import { menu } from "../../assets";
import { useMediaQuery } from "@react-hook/media-query";
import SearchBar from "../SearchBar";
function Header() {
  const { openCart, cartQuantity } = useCart();
  const { isNavOpen, openNav, closeNav } = useNav();
  const [showMenu, setShowMenu] = useState(false);

  const isDesktop = useMediaQuery("screen and (min-width: 720px)");
  useEffect(() => {
    if (isDesktop) {
      closeNav();
    }
  }, [isDesktop, closeNav]);
  const onNavClose = () => {
    const menuBtn = document.getElementById("menu-btn");
    menuBtn?.focus();
  };
  useEffect(() => {
    const handleScroll = () => {
      const header = document.querySelector(".header-container");
      const headerHeight = header ? header.getBoundingClientRect().height : 80;
      setShowMenu(window.scrollY > headerHeight);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);
  return (
    <header>
      {showMenu && (
        <button
          className="sticky-menu-btn"
          aria-controls="nav"
          aria-expanded={!isDesktop ? isNavOpen : undefined}
          id="sticky-menu-btn"
          onClick={openNav}
          style={{
            position: "fixed",
            top: 16,
            left: 16,
            zIndex: 1000,
            display: isDesktop ? "none" : "block",
          }}
        >
          <img className="icon" src={menu} alt="menu" />
        </button>
      )}
      <div className="header-container">
        <div className="header-left">
          <Link to={"/"}>
            {" "}
            <img className="logo" src={logo} alt="logo" />
          </Link>
          <div style={{ display: "flex" }}>
            <button id="sign-up">Sign Up</button>
            <button id="log-in">Log In</button>
          </div>
        </div>

        <div className="header-right">
          <button
            aria-controls="nav"
            aria-expanded={!isDesktop ? isNavOpen : undefined}
            id="menu-btn"
            onClick={openNav}
          >
            <img className="icon" src={menu} alt="menu" />
          </button>

          <SearchBar />
          <Link to={"/favorites"}>
            <img className="heart" src={heart} alt="heart" />
          </Link>
          <div style={{ position: "relative" }}>
            <button onClick={openCart}>
              <img className="cart" src={cartImg} alt="cart" />
            </button>
            {cartQuantity > 0 && (
              <button className="cart-badge" onClick={openCart}>
                {cartQuantity}
              </button>
            )}
          </div>
        </div>
      </div>
      <Navigation onClose={onNavClose} />
    </header>
  );
}

export default Header;
