import {
  Product,
  ProductBrand,
  ProductCategory,
  ProductFlag,
  ProductImage,
} from "../../../shared/types";
import api from "./axios";

export const fetchProductById = async (id: number): Promise<Product> => {
  const response = await api.get(`/products/${id}`);
  return response.data;
};

export const fetchProductImages = async (
  id: number
): Promise<ProductImage[]> => {
  const response = await api.get(`/products/${id}/images`);
  return response.data;
};

export const fetchProductFlags = async (id: number): Promise<ProductFlag[]> => {
  const response = await api.get(`/products/${id}/flags`);
  return response.data;
};

export const fetchProductCategory = async (
  id: number
): Promise<ProductCategory> => {
  const response = await api.get(`/products/${id}/category`);
  return response.data;
};

export const fetchProductSubcategories = async (
  id: number
): Promise<string[]> => {
  const response = await api.get(`/products/${id}/subcategories`);
  return response.data;
};

export const fetchProductBrand = async (id: number): Promise<ProductBrand> => {
  const response = await api.get(`/products/${id}/brand`);
  return response.data;
};
