import { ProductType } from "../../../shared/types/product";
import api from "./axios";
export const fetchProductsByCategory = async (
  category: string,
  subcategory?: string
): Promise<ProductType[]> => {
  if (subcategory) {
    const response = await api.get(`/shop/category/${category}/${subcategory}`);
    return response.data;
  }
  const response = await api.get(`/shop/category/${category}`);
  return response.data;
};
export const fetchProducts = async () => {
  const response = await api.get(`/shop`);
  return response.data;
};
export const fetchProduct = async (id: number): Promise<ProductType> => {
  const response = await api.get(`/product/${id}`);
  return response.data;
};
