import React, { ChangeEvent, useEffect, useState } from "react";
import { Category, Product } from "../../../../../shared/types";
import { useParams } from "react-router-dom";
import { fetchProduct, updateProduct } from "../../../api/admin/products";
import { fetchCategories } from "../../../api/categories";

const ProductPanel = () => {
  const [product, setProduct] = useState<Product | null>(null);
  const [categories, setCategories] = useState<Category[]>([]);

  const [error, setError] = useState<string | null>(null);
  const { id } = useParams();
  const [inputs, setInputs] = useState({
    name: null,
    price: null,
    description: null,
    brandId: null,
    subcategoryId: null,
    categoryId: null,
    discount: null,
    stockQuantity: null,
  });

  useEffect(() => {
    if (!id) {
      return;
    }
    fetchCategories()
      .then((res) => {
        console.log({ res });
        setCategories(res);
      })
      .catch((err) => {
        console.error(err);
        setError("Error fetching categories: " + err.message);
      });
    fetchProduct(parseInt(id))
      .then((res) => {
        setProduct(res);
      })
      .catch((err) => {
        console.error(err);
        setError("Error fetching product: " + err.message);
      });
  }, [id]);
  useEffect(() => {
    if (!product) return;
  }, [inputs, product]);
  if (error) {
    return (
      <div className="content-centered">
        <h1>Uh oh, an error occured.</h1>
        {error && <p>{error}</p>}
      </div>
    );
  }
  if (!product) {
    return (
      <div className="content-centered">
        <h1>Uh oh, we coudn't find this product.</h1>

        {error && <p>{error}</p>}
      </div>
    );
  }
  const handleChange = (e: FormEventHandler<HTMLFormElement>) => {
    setInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };
  const onSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    // setInputs({ ...inputs, ...e });
    console.log({ ...inputs, ...e.target });
    // updateProduct(product.id, );
  };
  return (
    <div>
      <h1>{product.name}</h1>
      <div className="content-body">
        <form method="put" onChange={handleChange} onSubmit={onSubmit}>
          <div>
            <label htmlFor="name">Product Name</label>

            <div className="flex-content">
              <input
                className="text-input"
                id="name"
                type="text"
                defaultValue={product.name}
              />
              <button type="submit" className="button-primary">
                Update
              </button>
            </div>
          </div>
        </form>
        <form method="put" onSubmit={onSubmit}>
          <div>
            <label htmlFor="name">Product Category</label>

            <div className="flex-content">
              <select
                className="text-input"
                id="categories"
                name="categoryId"
                defaultValue={product.category}
              >
                {categories.map((item) => (
                  <option key={item.id}>{item.name}</option>
                ))}
              </select>
              <button type="submit" className="button-primary">
                Update
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProductPanel;
