import { useEffect, useState } from "react";

interface UseAnimationToggleProps {
  value: boolean;
  duration?: number;
}
const useAnimationToggle = ({ value, duration }: UseAnimationToggleProps) => {
  const [endValue, setEndValue] = useState(false);
  useEffect(() => {
    if (value) {
      setTimeout(() => {
        setEndValue(true);
      }, duration || 300);
    } else {
      setTimeout(() => {
        setEndValue(false);
      }, duration || 300);
    }
  }, [value, duration]);
  return { endValue } as const;
};
export default useAnimationToggle;
