import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { formatCategory, formatCurrency } from "../../utils";
import "./ProductItem.css";
import { Product as ProductType } from "../../../../shared/types";
import { useProduct } from "../../hooks";

function Product({ id }: { id: string }) {
  const [showMoreSizes, setShowMoreSizes] = useState(false);
  const [selectedSize, setSelectedSize] = useState<number | null>(null);
  const productId = Number(id);
  const { loading, images, category, sizes, colors, brand, flags } =
    useProduct(productId);
  // const [selectedColor, setSelectedColor] = useState(item.colors[0]);
  const [error, setError] = useState("");
  // const { increaseCartQuantity } = useCart();
  // const [imageIndex, setImageIndex] = useState(0);
  const navigate = useNavigate();

  const onAddToCartPress = () => {
    setError("");
    if (!selectedSize) {
      return setError("Please select a size");
    }
    // if (!selectedColor) {
    //   return setError("Please select a color");
    // }

    // increaseCartQuantity({
    //   ...product,
    //   selectedColor,
    //   selectedSize,
    //   quantity: 1,
    // });
  };

  const onSizePress = (size: number) => {
    if (sizes.includes(size)) {
      setSelectedSize(size);
    }
  };
  if (loading) {
    return <div className="content-centered">Loading...</div>;
  }
  return (
    <div className="product">
      <div className="product-top">
        <div className="badge-container">
          {/* {product.isNew && <span className="new-badge">NEW</span>} */}
          {item.discount !== null && item.discount > 0 && (
            <span className="sale-badge">SALE</span>
          )}
        </div>

        <img
          className="large-img"
          // src={product.srcset[imageIndex]}
          alt={item.name}
        />

        <div className="thumbnail-images">
          {/* {product.srcset.length > 1 &&
            product.srcset.map((item, index) => (
              <button key={index} onClick={() => setImageIndex(index)}>
                <img className="img-thumbnail" src={item} alt={product.name} />
              </button>
            ))} */}
        </div>
      </div>

      <div className="product-bottom">
        <h2>{item.name}</h2>
        {category && (
          <p className="category">{formatCategory(category.name)}</p>
        )}

        {item.discount !== null && item.discount > 0 ? (
          <p className="price">
            {" "}
            <span className="old-price">
              {formatCurrency(item.price)}{" "}
            </span>{" "}
            <span className="sale-price">
              {formatCurrency(item.discount * item.price)}
            </span>
          </p>
        ) : (
          <p className="price">{formatCurrency(item.price)}</p>
        )}

        <div className="options">
          <div>
            <h4>
              Colors <span>(Please Select)</span>
            </h4>
            <div className="colors">
              {/* {item.colors.map((item: string, index: number) => (
                <div
                  key={index}
                  // className={
                  //   selectedColor === item ? "active-color-container" : ""
                  // }
                >
                  <button
                    onClick={() => setSelectedColor(item)}
                    // className={
                    //   selectedColor === item
                    //     ? "color-box active-color"
                    //     : "color-box"
                    // }
                    style={{ backgroundColor: item }}
                  />
                </div>
              ))} */}
            </div>
          </div>

          <div>
            <div className="size-heading">
              <h4>
                Sizes <span>(Please Select)</span>
              </h4>
              <button onClick={() => navigate("/size-guide", { state: item })}>
                Size Guide
              </button>
            </div>
            <div className="sizes">
              {/* {item.sizes.map((size, index) => {
                if (!showMoreSizes) {
                  return (
                    <button
                      onClick={() => onSizePress(size)}
                      key={index}
                      className={`size ${
                        !item.sizes.includes(size) ? "disabled-size" : ""
                      } ${selectedSize === size ? "active-size" : ""} `}
                    >
                      {size}
                    </button>
                  );
                } else {
                  return (
                    <button
                      onClick={() => onSizePress(size)}
                      key={index}
                      className={`size ${
                        !item.sizes.includes(size) ? "disabled-size" : ""
                      } ${selectedSize === size ? "active-size" : ""} `}
                    >
                      {size}
                    </button>
                  );
                }
              })} */}
              <button
                onClick={() => setShowMoreSizes(!showMoreSizes)}
                className="size-box"
              >
                {showMoreSizes ? "Show Less" : "Show More"}
              </button>
            </div>
          </div>
        </div>

        <div className="add-to-cart-container">
          <p className="error">{error} </p>
          <button onClick={onAddToCartPress}>Add to Cart</button>
        </div>
      </div>
    </div>
  );
}

export default Product;
