import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Product } from "../../../../shared/types/product";
import { fetchProducts, fetchProductsByCategory } from "../../api/products";
import { ProductGridItem } from "../../components";

const Shop: React.FC = () => {
  const { category } = useParams();
  console.log(category);
  const [products, setProducts] = useState<Product[]>([]);
  useEffect(() => {
    if (!category) return;
    fetchProducts()
      .then((res: Product[]) => {
        console.log(res.filter((item) => item.category === category));
        setProducts(res.filter((item) => item.category === category));
      })
      .catch((err) => console.error("Failed to fetch products:", err));
  }, [category]);
  return (
    <div className="shop-page" style={{ padding: "2rem" }}>
      <h1>
        Shop{" "}
        {category
          ? category?.charAt(0).toUpperCase() + category?.substring(1)
          : "All"}
      </h1>
      <p>Browse our latest footwear collection!</p>
      {/* Product listing will go here */}
      <div className="products-grid">
        {products.map((item) => (
          <ProductGridItem product={item} />
        ))}
      </div>
    </div>
  );
};

export default Shop;
