import React, { useEffect } from "react";

interface UseAnimationToggleProps {
  toggle: boolean;
  setToggle: React.Dispatch<React.SetStateAction<boolean>>;
  duration?: number;
}
const useAnimationToggle = ({
  toggle,
  setToggle,
  duration,
}: UseAnimationToggleProps) => {
  useEffect(() => {
    if (toggle) {
      setTimeout(() => {
        setToggle(false);
      }, duration || 300);
    } else {
      setTimeout(() => {
        setToggle(true);
      }, duration || 300);
    }
  }, [toggle, setToggle, duration]);
};
export default useAnimationToggle;
