import React, { useEffect, useRef, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Header.css";
import { heart, logo } from "../../assets";
import { useMediaQuery } from "@react-hook/media-query";
import SearchBar from "../SearchBar";
import { CartButton, MenuButton } from "../buttons/";
import NavBar from "../NavBar";
import SideBar from "../SideBar";
import { useCart } from "../../context";
import ShoppingCart from "../ShoppingCart";
function Header() {
  const [showMenu, setShowMenu] = useState(false);
  const [isNavOpen, setIsNavOpen] = useState(false);
  const navigate = useNavigate();
  const { openCart, isCartOpen, closeCart } = useCart();
  const menuButtonRef = useRef<HTMLButtonElement>(null);
  const cartButtonRef = useRef<HTMLButtonElement>(null);
  const isDesktop = useMediaQuery("screen and (min-width: 720px)");
  // useEffect(() => {
  //   if (isDesktop) {
  //     closeNav();
  //   }
  // }, [isDesktop, closeNav]);
  const onOpenNav = () => {
    setIsNavOpen(true);
  };
  const onCloseNav = () => {
    setIsNavOpen(false);
  };
  useEffect(() => {
    const handleScroll = () => {
      const header = document.querySelector("header");
      const headerHeight = header
        ? header.getBoundingClientRect().height + 100
        : 80;
      setShowMenu(window.scrollY > headerHeight);
      menuButtonRef.current?.focus();
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <header>
      <div hidden={true} aria-hidden={showMenu} className="header-container">
        <div className="header-left">
          <Link to={"/"}>
            {" "}
            <img className="logo" src={logo} alt="logo" />
          </Link>
          <div style={{ display: "flex" }}>
            <button
              tabIndex={showMenu ? -1 : undefined}
              aria-hidden={showMenu}
              id="sign-up"
            >
              Sign Up
            </button>
            <button id="log-in">Log In</button>
          </div>
        </div>

        <div className="header-right">
          <MenuButton
            aria-controls="nav"
            aria-expanded={!isDesktop ? isNavOpen : undefined}
            onClick={onOpenNav}
          />

          <SearchBar />
          <Link to={"/favorites"}>
            <img className="heart" src={heart} alt="heart" />
          </Link>
          <CartButton />
        </div>
      </div>

      <SideBar
        direction="right"
        triggerButton={cartButtonRef.current}
        onClose={closeCart}
        isOpen={isCartOpen}
      >
        <ShoppingCart
          onCheckoutPress={() => navigate("/checkout")}
          isCartOpen={isCartOpen}
        />
      </SideBar>

      <SideBar
        triggerButton={menuButtonRef.current}
        direction="left"
        onClose={onCloseNav}
        isOpen={isNavOpen}
      >
        <NavBar className="secondary" />
      </SideBar>

      {!showMenu && !isNavOpen && <NavBar className="main" />}

      <div
        className={`sticky-header
        ${showMenu ? "open" : "closed"}
          `}
      >
        <MenuButton
          ref={menuButtonRef}
          aria-controls="nav"
          aria-expanded={isNavOpen}
          onClick={onOpenNav}
        />
        <div className="content-row">
          <Link
            tabIndex={!showMenu ? -1 : undefined}
            aria-hidden={!showMenu}
            to={"/favorites"}
          >
            <img className="icon" src={heart} alt="heart" />
          </Link>
          <CartButton
            ref={cartButtonRef}
            onClick={openCart}
            badgeStyle={{ borderColor: "white" }}
          />
        </div>
      </div>
    </header>
  );
}

export default Header;
