import React, { Key } from "react";
import { Link } from "react-router-dom";
import "./ProductGridItem.css";
import { ProductType } from "../../../../shared/types/product";
import "../../styles/shared/_product.css";
import { formatCategory, formatCurrency } from "../../utils";

function ProductGridItem({ item }: { item: ProductType }) {
  return (
    <Link to={`/product/${item.id}`} state={item} className="product-grid-item">
      <div className="product-top">
        <div className="badge-container">
          {/* {item.isNew && <span className="new-badge">NEW</span>} */}
          {item.discount != null && <span className="sale-badge">SALE</span>}
        </div>

        <img src={item.image_url} alt={item.name} />
      </div>

      <div className="product-bottom">
        <h3>{item.name}</h3>
        <p className="type">{formatCategory(item.category)}</p>

        {item.discount ? (
          <p className="price">
            {" "}
            <span className="old-price">
              {formatCurrency(item.price)}{" "}
            </span>{" "}
            <span className="sale-price">
              {formatCurrency(item.price * item.discount)}
            </span>
          </p>
        ) : (
          <p className="price">{formatCurrency(item.price)}</p>
        )}

        {/* {item.reviews.length > 0 && (
          <div className="stars">
            {Array(
              Math.round(
                item.reviews.reduce((stars, item) => item.stars + stars, 0) /
                  item.reviews.length
              )
            )
              .fill("")
              .map((_, index) => (
                <span key={index}>&#9733;</span>
              ))}
          </div>
        )} */}
      </div>
    </Link>
  );
}

export default ProductGridItem;
