import { createContext, ReactNode, useContext, useState } from "react";
// import { useLocalStorage } from "../hooks/useLocalStorage";
import { ShoppingCart } from "../components";
import {  CartItem } from "../../../shared/types/product";


const useCart = ()  =>{
  return useContext(CartContext);
}
type CartContextType = {
  addItemToCart: (item: CartItem) => void;
  decreaseCartQuantity: (item: CartItem) => void;
  removeFromCart: (item: CartItem) => void;
  getItemQuantity: (item: CartItem) => number;
  cartItems: CartItem[];
  cartQuantity: number;
  openCart: () => void;
  closeCart: () => void;
  isCartOpen: boolean;
  totalPrice: number;
  checkout: boolean;
  setCheckout: React.Dispatch<React.SetStateAction<boolean>>;
  onCheckout: () => void;
};

const CartContext = createContext<CartContextType>({} as CartContextType);
const CartProvider = ({ children } : {children: ReactNode}) => {
  const [cartItems, setCartItems] = useState<CartItem[]>([]);
  const [isCartOpen, setIsCartOpen] = useState(false);
  const [checkout, setCheckout] = useState(false);

  const openCart = () => {
    setIsCartOpen(true);
  };

  const closeCart = () => {
    setIsCartOpen(false);
  };

  function isSameItem(item1: CartItem , item2: CartItem) {
    return (
      item1.id === item2.id &&
      item1.selectedColor === item2.selectedColor &&
      item1.selectedSize === item2.selectedSize
    );
  }
  function getItemQuantity(item: CartItem) {
    return cartItems.find((el) => isSameItem(el, item))?.quantity || 0;
  }
  function addItemToCart(item: CartItem) {
    setCartItems((currItems) => {
      if (currItems.find((el) => isSameItem(el, item)) == null) {
        return [...cartItems, { ...item, quantity: 1 }];
      } else {
        return cartItems.map((el) => {
          //if the item in the array is the same as the given item
          if (isSameItem(el, item)) {
            //then return that item with increased quantity
            return { ...el, quantity: el.quantity + 1 };
          } else {
            return el;
          }
        });
      }
    });
  }

  function decreaseCartQuantity(item: CartItem) {
    setCartItems((currItems) => {
      if (currItems.find((el) => isSameItem(el, item)) == null) {
        return cartItems.filter((el) => !isSameItem(el, item));
      } else {
        return cartItems.map((el) => {
          if (isSameItem(el, item)) {
            return { ...el, quantity: item.quantity - 1 };
          } else {
            return el;
          }
        });
      }
    });
  }
  function removeFromCart(item: CartItem) {
    setCartItems((currItems) => {
      return currItems.filter((el) => !isSameItem(item, el));
    });
  }

  function onCheckout() {
    setCheckout(false);
  }
  const cartQuantity = cartItems.reduce(
    (quantity, item) => item.quantity + quantity,
    0
  );
  const totalPrice = cartItems.reduce(
    (price, item) =>
      item?.salePrice
        ? item.salePrice * item.quantity + price
        : item.price * item.quantity + price,
    0
  );
  return (
    <CartContext.Provider
      value={{
         addItemToCart,
        decreaseCartQuantity,
        removeFromCart,
        getItemQuantity,
        cartItems,
        cartQuantity,
        openCart,
        closeCart,
        isCartOpen,
        totalPrice,
        checkout,
        setCheckout,
        onCheckout,
      }}
    >
      <ShoppingCart
        isCartOpen={isCartOpen}
        onCheckoutPress={() => {
          setIsCartOpen(false);
          setCheckout(true);
        }}
      />

      {children}
    </CartContext.Provider>
  );
}

export {CartProvider, useCart};
